function like(e) {

    e.preventDefault();

    $.ajax({
        method:'POST',
        url:'/likes',
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        },
        data: {
            id: $('#post_id').val(),
        },

        success:function(data) {
            if(data.hasLiked){
                $('#heart').html('<i class="liked-icon"></i>');
            }else{
                $('#heart').html('<i class="like-icon"></i>');
            }
            $('#like-area').text('Likes: '+data.likes)

        }
    });
}
